import React, { useState, useEffect, useRef } from 'react';

const WHITE_KEYS = [0, 2, 4, 5, 7, 9, 11];
const BLACK_KEYS = [1, 3, 6, 8, 10];
const isBlack = (note) => BLACK_KEYS.includes(note % 12);

const clamp = (value, min, max) => Math.min(Math.max(value, min), max);

const Key = ({ note, x, isBlackKey, isPressed, onNoteOn, onNoteOff }) => {
  const width = isBlackKey ? 0.6 : 1;
  const height = isBlackKey ? 0.5 : 0.2;
  const depth = isBlackKey ? 3 : 4.5;
  const color = isBlackKey ? 'black' : 'white';

  const pressDepth = 0.05;
  const pressTilt = isBlackKey ? 0.05 : 0.03;

  return (
    <group
      position={[x, 0, isBlackKey ? -0.75 : 0]}
      rotation={[isPressed ? pressTilt : 0, 0, 0]}
      onPointerDown={(e) => {
        e.stopPropagation();
        onNoteOn(note, 127);
      }}
      onPointerUp={(e) => {
        e.stopPropagation();
        onNoteOff(note);
      }}
      onPointerOut={(e) => {
        e.stopPropagation();
        onNoteOff(note);
      }}
    >
      <mesh position={[0, height / 2 - (isPressed ? pressDepth : 0), 0]}>
        <boxGeometry args={[width, height, depth]} />
        <meshStandardMaterial color={color} />
      </mesh>
    </group>
  );
};

const Keyboard3D = ({
  startNote = 21, // A0
  endNote = 108,  // C8
  onNoteOn,
  onNoteOff,
  activeNotes = new Set(),
}) => {
  const notes = Array.from({ length: endNote - startNote + 1 }, (_, i) => startNote + i);
  const whiteNotes = notes.filter(n => !isBlack(n));
  const blackNotes = notes.filter(n => isBlack(n));

  const keySpacing = 1.05;
  const totalWidth = whiteNotes.length * keySpacing; // This is the length of the keyboard
  const initialOffset = totalWidth / 2;

  const viewWidth = 10; // approx width/height in view for scrolling range
  const maxOffset = viewWidth / 2;
  const minOffset = -viewWidth / 2;

  const [keyboardOffset, setKeyboardOffset] = useState(0);
  const dragging = useRef(false);
  const lastX = useRef(null);
  const lastY = useRef(null); // For mobile dragging

  const [isMobile, setIsMobile] = useState(false);

  // Mobile detection
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Arrow keys
  useEffect(() => {
    const handleKey = (e) => {
      if (e.key === 'ArrowLeft') {
        setKeyboardOffset(offset => clamp(offset + 1, minOffset, maxOffset));
      } else if (e.key === 'ArrowRight') {
        setKeyboardOffset(offset => clamp(offset - 1, minOffset, maxOffset));
      }
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, [minOffset, maxOffset]);

  // Dragging
  useEffect(() => {
    const handleDown = (e) => {
      dragging.current = true;
      if (isMobile) {
        lastY.current = e.clientY;
      } else {
        lastX.current = e.clientX;
      }
    };
    const handleMove = (e) => {
      if (!dragging.current) return;
      if (isMobile) {
        const deltaY = e.clientY - lastY.current;
        setKeyboardOffset(offset =>
          // Drag down (increase clientY, positive deltaY) -> content moves down (keyboard offset decreases)
          clamp(offset - deltaY * 0.01, minOffset, maxOffset)
        );
        lastY.current = e.clientY;
      } else {
        const deltaX = e.clientX - lastX.current;
        setKeyboardOffset(offset =>
          // Drag right (increase clientX, positive deltaX) -> content moves right (keyboard offset increases)
          clamp(offset + deltaX * 0.01, minOffset, maxOffset)
        );
        lastX.current = e.clientX;
      }
    };
    const handleUp = () => {
      dragging.current = false;
    };

    window.addEventListener('mousedown', handleDown);
    window.addEventListener('mousemove', handleMove);
    window.addEventListener('mouseup', handleUp);
    return () => {
      window.removeEventListener('mousedown', handleDown);
      window.removeEventListener('mousemove', handleMove);
      window.removeEventListener('mouseup', handleUp);
    };
  }, [minOffset, maxOffset, isMobile]); // Added isMobile to dependencies

  // Mouse wheel
  useEffect(() => {
    const handleWheel = (e) => {
      setKeyboardOffset(offset =>
        clamp(offset - e.deltaY * 0.02, minOffset, maxOffset)
      );
    };
    window.addEventListener('wheel', handleWheel);
    return () => window.removeEventListener('wheel', handleWheel);
  }, [minOffset, maxOffset]);

  const rotationAngle = isMobile ? Math.PI / 2 : 0; // 90 degrees around Z for mobile
  const position = isMobile
    ? [8, -initialOffset + keyboardOffset, 0] // Original Y (-8) becomes new X (8), Original X becomes new Y
    : [-initialOffset + keyboardOffset, -8, 0];

  return (
    <group position={position} rotation={[0, 0, rotationAngle]}>
      {whiteNotes.map((note, i) => (
        <Key
          key={note}
          note={note}
          x={i * keySpacing}
          isBlackKey={false}
          isPressed={activeNotes.has(note)}
          onNoteOn={onNoteOn}
          onNoteOff={onNoteOff}
        />
      ))}
      {blackNotes.map((note) => {
        const index = whiteNotes.findIndex(n => n > note) - 1;
        const x = index * keySpacing + 0.65;
        return (
          <Key
            key={note}
            note={note}
            x={x}
            isBlackKey={true}
            isPressed={activeNotes.has(note)}
            onNoteOn={onNoteOn}
            onNoteOff={onNoteOff}
          />
        );
      })}
    </group>
  );
};

export default Keyboard3D;
