import React, { useState } from 'react';
import Keyboard3D from '../components/Keyboard3D';
import KnobPanel from '../components/KnobPanel';
import SliderPanel from '../components/SliderPanel';

const SceneManager = ({ activeNotes, onNoteOn, onNoteOff }) => {
  // Example control states
  const [cutoff, setCutoff] = useState(0.5);
  const [resonance, setResonance] = useState(0.4);
  const [volume, setVolume] = useState(0.8);

  const knobControls = [
    { id: 'cutoff', label: 'Cutoff', value: cutoff, min: 0, max: 1, onChange: setCutoff },
    { id: 'resonance', label: 'Resonance', value: resonance, min: 0, max: 1, onChange: setResonance },
  ];

  const sliderControls = [
    { id: 'volume', label: 'Volume', value: volume, min: 0, max: 1, onChange: setVolume },
  ];

  return (
    <>
      {/* Synth keyboard */}
      <Keyboard3D
        startNote={21} // A0
        endNote={108}  // C8
        onNoteOn={onNoteOn}
        onNoteOff={onNoteOff}
        activeNotes={activeNotes}
      />

      {/* Left-side knob panel */}
      <group position={[-8, 2, 0]}>
        <KnobPanel controls={knobControls} title="Filter Controls" />
      </group>

      {/* Right-side slider panel */}
      <group position={[8, 2, 0]}>
        <SliderPanel controls={sliderControls} title="Volume" />
      </group>
    </>
  );
};

export default SceneManager;
