import React, { useState, useEffect, useRef } from 'react';

const WHITE_KEYS = [0, 2, 4, 5, 7, 9, 11];
const BLACK_KEYS = [1, 3, 6, 8, 10];
const isBlack = (note) => BLACK_KEYS.includes(note % 12);
const clamp = (value, min, max) => Math.min(Math.max(value, min), max);

const Key = ({ note, x, isBlackKey, isPressed, onNoteOn, onNoteOff }) => {
  const width = isBlackKey ? 0.6 : 1;
  const height = isBlackKey ? 0.5 : 0.2;
  const depth = isBlackKey ? 3 : 4.5;
  const color = isBlackKey ? 'black' : 'white';
  const pressDepth = 0.05;
  const pressTilt = isBlackKey ? 0.05 : 0.03;

  return (
    <group
      position={[x, 0, isBlackKey ? -0.75 : 0]}
      rotation={[isPressed ? pressTilt : 0, 0, 0]}
      onPointerDown={(e) => {
        e.stopPropagation();
        onNoteOn(note, 127);
      }}
      onPointerUp={(e) => {
        e.stopPropagation();
        onNoteOff(note);
      }}
      onPointerOut={(e) => {
        e.stopPropagation();
        onNoteOff(note);
      }}
    >
      <mesh position={[0, height / 2 - (isPressed ? pressDepth : 0), 0]}>
        <boxGeometry args={[width, height, depth]} />
        <meshStandardMaterial color={color} />
      </mesh>
    </group>
  );
};

const Keyboard3D = ({
  startNote = 21,
  endNote = 108,
  onNoteOn,
  onNoteOff,
  activeNotes = new Set(),
}) => {
  const [scrollOffset, setScrollOffset] = useState(0);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const dragging = useRef(false);
  const lastPos = useRef(null);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const notes = Array.from({ length: endNote - startNote + 1 }, (_, i) => startNote + i);
  const whiteNotes = notes.filter(n => !isBlack(n));
  const blackNotes = notes.filter(n => isBlack(n));

  const spacing = 1.05;
  const totalWidth = whiteNotes.length * spacing;
  const viewLength = 20;
  const maxOffset = 0;
  const minOffset = -totalWidth + viewLength;

  useEffect(() => {
    const handleKey = (e) => {
      if (e.key === 'ArrowLeft' || e.key === 'ArrowUp') {
        setScrollOffset(offset => clamp(offset + 1, minOffset, maxOffset));
      } else if (e.key === 'ArrowRight' || e.key === 'ArrowDown') {
        setScrollOffset(offset => clamp(offset - 1, minOffset, maxOffset));
      }
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, [minOffset, maxOffset]);

  useEffect(() => {
    const handleDown = (e) => {
      dragging.current = true;
      lastPos.current = isMobile ? e.clientY : e.clientX;
    };
    const handleMove = (e) => {
      if (!dragging.current) return;
      const current = isMobile ? e.clientY : e.clientX;
      const delta = current - lastPos.current;
      setScrollOffset(offset => clamp(offset + delta * 0.01, minOffset, maxOffset));
      lastPos.current = current;
    };
    const handleUp = () => (dragging.current = false);

    window.addEventListener('mousedown', handleDown);
    window.addEventListener('mousemove', handleMove);
    window.addEventListener('mouseup', handleUp);
    return () => {
      window.removeEventListener('mousedown', handleDown);
      window.removeEventListener('mousemove', handleMove);
      window.removeEventListener('mouseup', handleUp);
    };
  }, [isMobile, minOffset, maxOffset]);

  useEffect(() => {
    const handleWheel = (e) => {
      const delta = isMobile ? e.deltaY : e.deltaY;
      setScrollOffset(offset => clamp(offset - delta * 0.02, minOffset, maxOffset));
    };
    window.addEventListener('wheel', handleWheel);
    return () => window.removeEventListener('wheel', handleWheel);
  }, [isMobile, minOffset, maxOffset]);

  const keyboardPosition = isMobile
    ? [0, scrollOffset - 8, -10] // scrolls vertically
    : [-totalWidth / 2 + scrollOffset, -8, 0]; // scrolls horizontally

  return (
    <group position={keyboardPosition}>
      {whiteNotes.map((note, i) => (
        <Key
          key={note}
          note={note}
          x={i * spacing}
          isBlackKey={false}
          isPressed={activeNotes.has(note)}
          onNoteOn={onNoteOn}
          onNoteOff={onNoteOff}
        />
      ))}
      {blackNotes.map((note) => {
        const index = whiteNotes.findIndex(n => n > note) - 1;
        const x = index * spacing + 0.65;
        return (
          <Key
            key={note}
            note={note}
            x={x}
            isBlackKey={true}
            isPressed={activeNotes.has(note)}
            onNoteOn={onNoteOn}
            onNoteOff={onNoteOff}
          />
        );
      })}
    </group>
  );
};

export default Keyboard3D;
