import React, { useState } from 'react';
import { useThree } from '@react-three/fiber';
import KnobPanel from '../components/KnobPanel';
import SliderPanel from '../components/SliderPanel';

const SceneManager = () => {
  const [cutoff, setCutoff] = useState(0.5);
  const [resonance, setResonance] = useState(0.3);
  const [volume, setVolume] = useState(0.8);

  const knobControls = [
    { id: 'cutoff', label: 'Cutoff', value: cutoff, min: 0, max: 1, onChange: setCutoff },
    { id: 'resonance', label: 'Resonance', value: resonance, min: 0, max: 1, onChange: setResonance },
  ];

  const sliderControls = [
    { id: 'volume', label: 'Volume', value: volume, min: 0, max: 1, onChange: setVolume },
  ];

  return (
    <>
      {/* Positioning panels in space */}
      <group position={[-5, 0, 0]}>
        <KnobPanel controls={knobControls} />
      </group>

      <group position={[5, 0, 0]}>
        <SliderPanel controls={sliderControls} />
      </group>
    </>
  );
};

export default SceneManager;
