import React, { useState } from 'react';

const WHITE_KEYS = [0, 2, 4, 5, 7, 9, 11];
const BLACK_KEYS = [1, 3, 6, 8, 10];

const isBlack = (note) => BLACK_KEYS.includes(note % 12);

const Key = ({ note, x, isBlackKey, onNoteOn, onNoteOff }) => {
  const [pressed, setPressed] = useState(false);

  const width = isBlackKey ? 0.6 : 1;
  const height = isBlackKey ? 0.5 : 0.2;
  const depth = isBlackKey ? 3 : 4.5;
  const color = isBlackKey ? 'black' : 'white';

  const pressDepth = 0.05;
  const pressTilt = isBlackKey ? 0.05 : 0.03;

  return (
    <group
      position={[x, 0, isBlackKey ? -0.75 : 0]}
      rotation={[pressed ? pressTilt : 0, 0, 0]}
      onPointerDown={(e) => {
  e.stopPropagation();
  setPressed(true);
  onNoteOn(note, 127);
}}
onPointerUp={(e) => {
  e.stopPropagation();
  setPressed(false);
  onNoteOff(note);
}}
onPointerOut={(e) => {
  e.stopPropagation();
  setPressed(false);
  onNoteOff(note);
}}

    >
      <mesh position={[0, height / 2 - (pressed ? pressDepth : 0), 0]}>
        <boxGeometry args={[width, height, depth]} />
        <meshStandardMaterial color={color} />
      </mesh>
    </group>
  );
};


const Keyboard3D = ({ startNote = 48, endNote = 72, onNoteOn, onNoteOff }) => {
  const notes = Array.from({ length: endNote - startNote + 1 }, (_, i) => startNote + i);
  const whiteNotes = notes.filter(n => !isBlack(n));
  const blackNotes = notes.filter(n => isBlack(n));

  const keySpacing = 1.05;
  const offset = (whiteNotes.length * keySpacing) / 2;

  return (
    <group position={[-offset, 0, 0]}>
      {whiteNotes.map((note, i) => (
        <Key
          key={note}
          note={note}
          x={i * keySpacing}
          isBlackKey={false}
          onNoteOn={onNoteOn}
          onNoteOff={onNoteOff}
        />
      ))}
      {blackNotes.map((note) => {
        const index = whiteNotes.findIndex(n => n > note) - 1;
        const x = index * keySpacing + 0.65;
        return (
          <Key
            key={note}
            note={note}
            x={x}
            isBlackKey={true}
            onNoteOn={onNoteOn}
            onNoteOff={onNoteOff}
          />
        );
      })}
    </group>
  );
};

export default Keyboard3D;
