import React, { useState, useEffect, useRef } from 'react';

const WHITE_KEYS = [0, 2, 4, 5, 7, 9, 11];
const BLACK_KEYS = [1, 3, 6, 8, 10];
const isBlack = (note) => BLACK_KEYS.includes(note % 12);
const clamp = (value, min, max) => Math.min(Math.max(value, min), max);

const Key = ({ note, x, y, isBlackKey, isPressed, onNoteOn, onNoteOff, isMobile }) => {
  const width = isBlackKey ? 0.6 : 1;
  const height = isBlackKey ? 0.5 : 0.2;
  const depth = isBlackKey ? 3 : 4.5;
  const color = isBlackKey ? 'black' : 'white';

  const pressDepth = 0.05;
  const pressTilt = isBlackKey ? 0.05 : 0.03;

  const groupPosition = isMobile
    ? [0, y, isBlackKey ? -0.75 : 0]
    : [x, 0, isBlackKey ? -0.75 : 0];

  const groupRotation = isMobile ? [0, 0, -Math.PI / 2] : [isPressed ? pressTilt : 0, 0, 0];
  const meshRotation = isMobile ? [isPressed ? pressTilt : 0, 0, 0] : null;

  return (
    <group
      position={groupPosition}
      rotation={groupRotation}
      onPointerDown={(e) => {
        e.stopPropagation();
        onNoteOn(note, 127);
      }}
      onPointerUp={(e) => {
        e.stopPropagation();
        onNoteOff(note);
      }}
      onPointerOut={(e) => {
        e.stopPropagation();
        onNoteOff(note);
      }}
    >
      <mesh
        position={[0, height / 2 - (isPressed ? pressDepth : 0), 0]}
        rotation={meshRotation}
      >
        <boxGeometry args={[width, height, depth]} />
        <meshStandardMaterial color={color} />
      </mesh>
    </group>
  );
};

const Keyboard3D = ({
  startNote = 21,
  endNote = 108,
  onNoteOn,
  onNoteOff,
  activeNotes = new Set(),
}) => {
  const [keyboardOffset, setKeyboardOffset] = useState(0);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  const notes = Array.from({ length: endNote - startNote + 1 }, (_, i) => startNote + i);
  const whiteNotes = notes.filter(n => !isBlack(n));
  const blackNotes = notes.filter(n => isBlack(n));

  const spacing = 1.05;
  const totalLength = whiteNotes.length * spacing;
  const viewLength = 20;
  const maxOffset = 0;
  const minOffset = -totalLength + viewLength;

  const dragging = useRef(false);
  const lastPos = useRef(null);

  useEffect(() => {
    const updateSize = () => {
      setIsMobile(window.innerWidth < 768);
    };
    window.addEventListener('resize', updateSize);
    return () => window.removeEventListener('resize', updateSize);
  }, []);

  useEffect(() => {
    const handleKey = (e) => {
      if (e.key === 'ArrowLeft' || e.key === 'ArrowUp') {
        setKeyboardOffset(offset => clamp(offset + 1, minOffset, maxOffset));
      } else if (e.key === 'ArrowRight' || e.key === 'ArrowDown') {
        setKeyboardOffset(offset => clamp(offset - 1, minOffset, maxOffset));
      }
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, [minOffset, maxOffset]);

  useEffect(() => {
    const handleDown = (e) => {
      dragging.current = true;
      lastPos.current = isMobile ? e.clientY : e.clientX;
    };
    const handleMove = (e) => {
      if (!dragging.current) return;
      const current = isMobile ? e.clientY : e.clientX;
      const delta = current - lastPos.current;
      setKeyboardOffset(offset =>
        clamp(offset + delta * 0.01, minOffset, maxOffset)
      );
      lastPos.current = current;
    };
    const handleUp = () => {
      dragging.current = false;
    };

    window.addEventListener('mousedown', handleDown);
    window.addEventListener('mousemove', handleMove);
    window.addEventListener('mouseup', handleUp);
    return () => {
      window.removeEventListener('mousedown', handleDown);
      window.removeEventListener('mousemove', handleMove);
      window.removeEventListener('mouseup', handleUp);
    };
  }, [isMobile, minOffset, maxOffset]);

  useEffect(() => {
    const handleWheel = (e) => {
      setKeyboardOffset(offset =>
        clamp(offset - (isMobile ? e.deltaY : e.deltaY * 0.5) * 0.02, minOffset, maxOffset)
      );
    };
    window.addEventListener('wheel', handleWheel);
    return () => window.removeEventListener('wheel', handleWheel);
  }, [isMobile, minOffset, maxOffset]);

  const basePosition = isMobile ? [0, -8, 0] : [-totalLength / 2 + keyboardOffset, -8, 0];

  return (
    <group position={basePosition}>
      {whiteNotes.map((note, i) => {
        const pos = i * spacing + keyboardOffset;
        return (
          <Key
            key={note}
            note={note}
            x={pos}
            y={pos}
            isBlackKey={false}
            isPressed={activeNotes.has(note)}
            onNoteOn={onNoteOn}
            onNoteOff={onNoteOff}
            isMobile={isMobile}
          />
        );
      })}
      {blackNotes.map((note) => {
        const index = whiteNotes.findIndex(n => n > note) - 1;
        const pos = index * spacing + 0.65;
        return (
          <Key
            key={note}
            note={note}
            x={pos}
            y={pos}
            isBlackKey={true}
            isPressed={activeNotes.has(note)}
            onNoteOn={onNoteOn}
            onNoteOff={onNoteOff}
            isMobile={isMobile}
          />
        );
      })}
    </group>
  );
};

export default Keyboard3D;
